import { createClient } from '@supabase/supabase-js';
import type { Database } from './types';

const SUPABASE_URL = import.meta.env.VITE_SUPABASE_URL as string;
const SUPABASE_PUBLISHABLE_KEY = import.meta.env.VITE_SUPABASE_ANON_KEY as string;

// Import the supabase client like this:
// import { supabase } from "@/integrations/supabase/client";

export const supabase = createClient<Database>(SUPABASE_URL, SUPABASE_PUBLISHABLE_KEY);

//console.log('SUPABASE_URL : ',SUPABASE_URL)
// Custom function to handle the find_invitation_by_code function
export const findInvitationByCode = async (code: string) => {
  const { data, error } = await supabase.rpc('find_invitation_by_code', {
    code_param: code,
  });

  if (error) {
    console.error('Error in findInvitationByCode:', error);
    return { data: null, error };
  }

  return { data, error: null };
};

export const fetchCountries = async (projectId: string) => {
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  const { data, error } = await (supabase as any)
    .from('project_allowed_countries')
    .select(`
      country_id,
      countries (
        id,
        name_es,
        iso2,
        phone_code
      )
    `)
    .eq('project_id', projectId);

  if (error) {
    console.error('Error fetching allowed countries:', error);
    return [];
  }

  return data; // ðŸ‘ˆ devuelve los datos
};


